/**
 * HotelListingPage Component
 * 
 * A comprehensive hotel listing page featuring:
 * - Universal Navbar with glassmorphism and "Big Pill" search
 * - Super Hotel Cards with "Soft Precision" design
 * - Responsive grid layout
 * - Gen Z & Senior friendly UI elements
 */

import React, { useState, useEffect } from 'react';
import { 
  MapPin, 
  Star, 
  Heart, 
  Search, 
  Calendar, 
  Users, 
  Filter, 
  ChevronDown,
  Wifi,
  Coffee,
  Car,
  Utensils
} from 'lucide-react';
import { motion, AnimatePresence } from 'framer-motion';
import { clsx, type ClassValue } from 'clsx';
import { twMerge } from 'tailwind-merge';

// --- Utils ---
function cn(...inputs: ClassValue[]) {
  return twMerge(clsx(inputs));
}

// --- Types ---
interface Hotel {
  id: string;
  name: string;
  location: string;
  rating: number;
  reviews: number;
  price: number;
  image: string;
  badges: string[];
  amenities: string[];
  isFavorite: boolean;
  isLimitedTime?: boolean;
}

// --- Data ---
