    badges: ["All Inclusive", "Guided Tours"],
    amenities: ["wifi", "dining", "adventure"],
    isFavorite: true
  }
];

// --- Components ---

const Badge = ({ children, variant = 'default' }: { children: React.ReactNode, variant?: 'default' | 'accent' }) => {
  return (
    <span className={cn(
      "inline-flex items-center px-3 py-1 rounded-full text-xs font-semibold tracking-wide transition-colors",
      variant === 'default' 
        ? "bg-secondary text-secondary-foreground" 
        : "bg-accent/10 text-accent"
    )}>
      {children}
    </span>
  );
};

const Navbar = () => {
  return (
    <nav className="sticky top-4 z-50 mx-auto max-w-7xl px-4 md:px-6 mb-8">
      <div className="bg-white/80 backdrop-blur-xl border border-white/20 shadow-lg rounded-full px-6 py-3 flex items-center justify-between transition-all duration-300">
        {/* Logo Area */}
        <div className="flex items-center gap-2">
          <div className="w-8 h-8 rounded-full bg-primary flex items-center justify-center text-white">
            <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="3" className="w-5 h-5">
              <path d="M3 21h18M5 21V7l8-4 8 4v14" strokeLinecap="round" strokeLinejoin="round"/>
            </svg>
          </div>
          <span className="text-xl font-bold tracking-tight text-slate-900 hidden md:block">
            Stay<span className="text-primary">Super</span>
          </span>
        </div>

        {/* Big Pill Search */}
        <div className="hidden md:flex items-center bg-secondary/50 rounded-full border border-slate-200 p-1 shadow-inner max-w-2xl w-full mx-4">
          <div className="flex-1 flex items-center px-4 border-r border-slate-300">
            <MapPin className="w-4 h-4 text-slate-400 mr-2" />
            <div className="flex flex-col">
              <span className="text-[10px] uppercase font-bold text-slate-400 tracking-wider">Location</span>
              <span className="text-sm font-semibold text-slate-800">Anywhere</span>
            </div>
          </div>
          <div className="flex-1 flex items-center px-4 border-r border-slate-300">
            <Calendar className="w-4 h-4 text-slate-400 mr-2" />
            <div className="flex flex-col">
              <span className="text-[10px] uppercase font-bold text-slate-400 tracking-wider">Dates</span>
              <span className="text-sm font-semibold text-slate-800">Add dates</span>
            </div>
          </div>
          <div className="flex-1 flex items-center px-4">
            <Users className="w-4 h-4 text-slate-400 mr-2" />
            <div className="flex flex-col">
              <span className="text-[10px] uppercase font-bold text-slate-400 tracking-wider">Guests</span>
              <span className="text-sm font-semibold text-slate-800">Add guests</span>
            </div>
          </div>
          <button className="bg-primary hover:bg-primary/90 text-white p-3 rounded-full shadow-lg transition-transform hover:scale-105 active:scale-95 ml-2">
            <Search className="w-5 h-5" />
          </button>
        </div>

        {/* Mobile Search Trigger */}
        <div className="md:hidden flex-1 flex justify-end mr-2">
          <button className="bg-secondary/80 p-2 rounded-full">
            <Search className="w-5 h-5 text-slate-700" />
          </button>
        </div>

        {/* User Actions */}
        <div className="flex items-center gap-2">
          <button className="p-2 rounded-full hover:bg-secondary text-slate-600 transition-colors">
            <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" className="w-5 h-5">
              <path d="M12 21a9 9 0 1 0-9-9 9 7.94 7.94 0 0 0 9 9Z" />
            </svg>
          </button>
          <button className="hidden sm:flex items-center gap-2 px-4 py-2 bg-slate-900 text-white rounded-full text-sm font-semibold hover:bg-slate-800 transition-colors shadow-md">
            Sign In
          </button>
        </div>
      </div>
