};

const HotelCardSkeleton = () => {
  return (
    <div className="bg-white rounded-[1.5rem] overflow-hidden shadow-sm border border-slate-100 flex flex-col h-full animate-pulse">
      <div className="aspect-[4/3] bg-slate-200 relative"></div>
      <div className="p-5 flex flex-col flex-grow gap-3">
        <div className="h-7 bg-slate-200 rounded-md w-3/4"></div>
        <div className="h-4 bg-slate-200 rounded-md w-1/2"></div>
        <div className="flex gap-2 mt-2">
          <div className="h-6 bg-slate-200 rounded-full w-20"></div>
          <div className="h-6 bg-slate-200 rounded-full w-20"></div>
        </div>
        <div className="mt-auto flex justify-between items-end pt-4">
          <div className="h-10 bg-slate-200 rounded-full w-28"></div>
          <div className="h-8 bg-slate-200 rounded-md w-24"></div>
        </div>
      </div>
    </div>
  );
};

const HotelCard = ({ hotel }: { hotel: Hotel }) => {
  const [isHovered, setIsHovered] = useState(false);
  const [isLiked, setIsLiked] = useState(hotel.isFavorite);

  return (
    <motion.div 
      className="group relative bg-white rounded-[1.5rem] overflow-hidden shadow-lg hover:shadow-xl transition-all duration-300 border border-slate-100 flex flex-col h-full"
      whileHover={{ y: -4, scale: 1.01 }}
      onHoverStart={() => setIsHovered(true)}
      onHoverEnd={() => setIsHovered(false)}
    >
      {/* Image Area */}
      <div className="relative aspect-[4/3] overflow-hidden bg-slate-100">
        <motion.img 
          src={hotel.image} 
          alt={hotel.name}
          className="w-full h-full object-cover transition-transform duration-700"
          animate={{ scale: isHovered ? 1.08 : 1 }}
        />

        {/* Heart Button */}
        <button 
          onClick={(e) => {
            e.preventDefault();
            setIsLiked(!isLiked);
          }}
          className="absolute top-4 right-4 p-3 bg-white/90 backdrop-blur-sm rounded-full shadow-md hover:scale-110 active:scale-95 transition-all z-10"
          aria-label={isLiked ? "Remove from favorites" : "Add to favorites"}
        >
          <Heart 
            className={cn(
              "w-5 h-5 transition-colors duration-300", 
              isLiked ? "fill-rose-500 text-rose-500" : "text-slate-600"
            )} 
          />
        </button>

        {/* Limited Time Badge */}
        {hotel.isLimitedTime && (
          <div className="absolute top-4 left-4">
             <span className="bg-accent text-white px-3 py-1.5 rounded-full text-xs font-bold tracking-wide shadow-md flex items-center gap-1">
               <span className="w-1.5 h-1.5 bg-white rounded-full animate-pulse"></span>
               Limited Deal
             </span>
          </div>
        )}
      </div>

      {/* Content Area */}
      <div className="p-5 flex flex-col flex-grow bg-white">
        {/* Row 1: Name & Rating */}
        <div className="flex justify-between items-start mb-1">
          <h3 className="text-xl font-bold text-slate-900 leading-tight group-hover:text-primary transition-colors">
            {hotel.name}
          </h3>
          <div className="flex items-center gap-1 bg-slate-50 px-2 py-1 rounded-lg">
            <Star className="w-3.5 h-3.5 fill-amber-400 text-amber-400" />
            <span className="text-sm font-bold text-slate-900">{hotel.rating}</span>
          </div>
        </div>

        {/* Row 2: Location */}
        <div className="flex items-center gap-1.5 mb-3 text-slate-500">
          <MapPin className="w-4 h-4 flex-shrink-0" />
          <span className="text-sm font-medium truncate">{hotel.location}</span>
        </div>

        {/* Row 3: Badges */}
        <div className="flex flex-wrap gap-2 mb-4">
          {hotel.badges.map((badge, i) => (
            <Badge key={i} variant={i === 0 && hotel.isLimitedTime ? 'accent' : 'default'}>
              {badge}
            </Badge>
          ))}
        </div>

        {/* Row 4: Pricing & Action (The Kicker) */}
        <div className="mt-auto pt-4 border-t border-slate-100 flex items-center justify-between gap-4">
          <motion.button
            className="flex-1 bg-primary text-white py-3 px-5 rounded-xl font-bold text-sm shadow-md shadow-primary/20 hover:bg-primary/90 hover:shadow-lg hover:shadow-primary/30 active:scale-95 transition-all flex items-center justify-center gap-2"
            whileTap={{ scale: 0.98 }}
          >
            Book Now
          </motion.button>

          <div className="text-right">
            <span className="block text-xs text-slate-400 font-medium">starting from</span>
            <div className="flex items-baseline justify-end gap-1">
              <span className="text-2xl font-bold text-slate-900">${hotel.price}</span>
              <span className="text-sm text-slate-500 font-medium">/night</span>
            </div>
          </div>
        </div>
      </div>
    </motion.div>
  );
};

export function HotelListingPage() {
  const [isLoading, setIsLoading] = useState(true);
  const [hotels, setHotels] = useState<Hotel[]>([]);

  useEffect(() => {
    // Simulate API fetch with delay for skeleton demo
    const timer = setTimeout(() => {
      setHotels(MOCK_HOTELS);
      setIsLoading(false);
    }, 1500);

    return () => clearTimeout(timer);
  }, []);

  return (
    <div className="min-h-screen bg-[#f8fafc] font-sans pb-20">
      <Navbar />

      <main className="max-w-7xl mx-auto px-4 md:px-6">
        {/* Header Section */}
        <header className="mb-10 mt-4 flex flex-col md:flex-row md:items-end justify-between gap-4">
          <div>
            <h1 className="text-3xl md:text-4xl font-extrabold text-slate-900 tracking-tight mb-2">
              Find your next <span className="text-primary">experience</span>
            </h1>
            <p className="text-slate-500 text-lg max-w-xl">
              Discover top-rated hotels, resorts, and vacation homes perfect for your getaway.
            </p>
          </div>

          {/* Filter Toggle */}
          <button className="flex items-center gap-2 px-5 py-2.5 bg-white border border-slate-200 rounded-full font-semibold text-slate-700 shadow-sm hover:bg-slate-50 transition-colors self-start md:self-auto">
            <Filter className="w-4 h-4" />
            <span>Filters</span>
          </button>
        </header>

        {/* Grid Section */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
          <AnimatePresence mode="wait">
            {isLoading ? (
              // Skeleton Loading State
              Array.from({ length: 6 }).map((_, i) => (
                <motion.div
                  key={`skeleton-${i}`}
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  exit={{ opacity: 0 }}
                  transition={{ duration: 0.3, delay: i * 0.05 }}
                >
                  <HotelCardSkeleton />
                </motion.div>
              ))
            ) : (
              // Actual Hotel Cards
              hotels.map((hotel, index) => (
                <motion.div
                  key={hotel.id}
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ duration: 0.5, delay: index * 0.1 }}
                >
                  <HotelCard hotel={hotel} />
                </motion.div>
              ))
            )}
          </AnimatePresence>
        </div>

        {/* Empty State / End of List */}
        {!isLoading && hotels.length > 0 && (
          <div className="mt-16 text-center">
            <button className="inline-flex items-center gap-2 text-primary font-bold text-lg hover:underline underline-offset-4">
              Show more results
              <ChevronDown className="w-5 h-5" />
            </button>
          </div>
        )}
      </main>
    </div>
  );
}

export default HotelListingPage;
